set serveroutput ondeclarel_custid customer.custid%type;l_facility facility.facility%type;beginl_custid := &&custid;l_facility := &&facility;insert into bbb_routing_parmsvalues(l_custid,l_facility,'(DEFAULT)','(DEFAULT)','N','S',trunc(sysdate),1,30,0,999999,0,999999,'SYNAPSE',sysdate);insert into bbb_routing_parmsvalues(l_custid,l_facility,'(DEFAULT)','(DEFAULT)','Y','L',trunc(sysdate),16,999999,151,4999,0,749,'SYNAPSE',sysdate);insert into bbb_routing_parmsvalues(l_custid,l_facility,'(DEFAULT)','(DEFAULT)','Y','T',trunc(sysdate),16,999999,5000,999999,750,999999,'SYNAPSE',sysdate);end;/exit;