/* get rid of data in all non-config tables** (e.g. can be used to clear out activity from** a prod environment that was just established** from the data in a test environment**** Also run the following scripts after this script is run:** auditlocation.sql** synclocation.sql*/truncate table alert_history;truncate table alert_manager;truncate table applocks;truncate table appmsgs;truncate table asofinventory;truncate table asofinventorydtl;truncate table commitments;truncate table custbilldates;truncate table custitemcount;truncate table custitemtot;truncate table cyclecountactivity;truncate table daily_billing_run;truncate table deletedplate;update door   set loadno = 0,       lastuser = 'SYNAPSE',       lastupdate = sysdate where nvl(loadno,0) != 0;truncate table imt_log;truncate table invadjactivity;truncate table invsession;truncate table invoicedtl;truncate table invoicehdr;truncate table itemdemand;truncate table loads;truncate table loadstop;truncate table loadstopship;truncate table multishipdtl;truncate table multishiphdr;truncate table orderhdr;truncate table neworderdtl;truncate table neworderhdr;truncate table oldexp_chunks;truncate table oldexp_definitions;truncate table oldexp_lines;truncate table oldorderdtl;truncate table oldorderhdr;truncate table orderhdrbolcomments;truncate table ordercheck;truncate table orderdtl;truncate table orderdtlline;truncate table orderdtlrcpt;truncate table orderdtlbolcomments;truncate table orderhistory;truncate table orderlabor;truncate table pimevents;truncate table plate;truncate table platehistory;truncate table postdtl;truncate table posthdr;truncate table shippingplate;truncate table shippingplatehistory;truncate table subtaskhistory;truncate table subtasks;truncate table taskhistory;truncate table tasks;truncate table userhistory;exit;